<div class="item"><a href="/u/{php echo urlencode($v['user']);}/post.html" target="_blank"><img src="{#bucketcdn}{$v.avatar}_50" width="50" height="50" data-uid="{$v.uid}" class="circle pull-left js-info" alt="{$v['user']}"></a><div class="middle text"><p ><a href="javascript:void(0);" onclick="pl_pid({$v['id']})" class="author">@{$v.user}</a><div class="text typo editor-style"><?php echo nl2br($v['content']);?></div><span class="time">{php echo humandate($v['atime'])} </span><div class="pull-right tags2"><a style="margin-right: 15px;" class="likersc fa fa-thumbs-up" href="javascript:void(0);" onclick="vote({$v['id']},'Comment','goods');">{$v.goods}</a><a style="margin-right: 15px;" class="likersc fa fa-thumbs-down" href="javascript:void(0);" onclick="vote({$v['id']},'Comment','nos');">{$v.nos}</a><a class="postsc fa fa-commenting-o" {if $v['posts']>0}href="/Comment/more/{$v['id']}.html"{/if}>{$v.posts}</a></div></div></div>